import React, {Component} from 'react';
import './App.css';
import 'nes.css/css/nes.min.css'

import axios from 'axios';


class App extends Component {

    state = {
        user: '',
        password: ''
    };

    fetchData() {
        axios.post(`http://localhost:4000`, {
            user: this.state.user,
            password: this.state.password
        }).then(res => {
            console.log(res)
        });
    }

    updateUsername = (event) => this.setState({user: event.target.value});
    updatePassword = (event) => this.setState({password: event.target.value});

    render() {
        return (
            <div className="App">
                <section className="nes-container with-title login-container">
                    <h2 className="title">Login Page</h2>
                    <div className="containers">

                        <p className={'caption'}>Please Enter your info to see the attendance</p>
                        <div className={'wrapper'}>
                            <i className="nes-kirby"/>
                        </div>

                        <div className="nes-field">
                            <label htmlFor="name_field">Username</label>
                            <input type="text" onChange={this.updateUsername} className="nes-input"/>

                            <label htmlFor="name_field">Password</label>
                            <input type="password" onChange={this.updatePassword} className="nes-input"/>
                        </div>

                        <br/>

                        <div className={'wrapper'}>
                            <button type="button" className="nes-btn" onClick={this.fetchData}>Login</button>
                        </div>
                    </div>
                </section>

                <section className="nes-container is-rounded loading-container">
                    <h5>Loading</h5>
                    <img src="https://mir-s3-cdn-cf.behance.net/project_modules/disp/92b69c22219753.5630ea254987a.gif"
                         alt="" width={200}/>
                </section>
            </div>
        );
    }
}

export default App;
