import React, {Component} from 'react';
import './App.css'

class Results extends Component {

    loadingImages = [

    ];

    render() {
        const {name, dat} = this.props.student;
        return (
            <>
                <section className="nes-container with-title login-container">
                    <h2 className="title">Results</h2>
                    <h6>Welcome {name}</h6>

                    <div className={'wrapper'}>
                        <img
                            src="https://thumbs.gfycat.com/HopefulShockedBarracuda-small.gif"
                            width={150}/>
                    </div>

                    <div className="nes-table-responsive">
                        <table className="nes-table is-bordered is-centered">
                            <thead>
                            <tr>
                                <th>Course</th>
                                <th>Attendance</th>
                            </tr>
                            </thead>

                            <tbody>
                            {
                                dat.map((item, i) =>
                                    <tr key={i}>
                                        <td>{item.courseName}</td>
                                        <td>{item.attendace}</td>
                                    </tr>
                                )
                            }
                            </tbody>
                        </table>
                    </div>

                    <br/>
                    <br/>

                    <div className={'wrapper'}>
                        <button type="button" className="nes-btn is-primary" onClick={this.props.reset}>Return</button>
                    </div>

                </section>
            </>
        );
    }
}

export default Results;
