import React, {Component} from 'react';
import './App.css';
import 'nes.css/css/nes.min.css'

import axios from 'axios';


class App extends Component {
    fetchData() {
        axios.post(`localhost:4000`, {
            username: '3500190',
            password: '1093866620'
        }).then(res => {
            const posts = res.data.data.children.map(obj => obj.data);
            this.setState({posts});
        });
    }

    render() {
        return (
            <div className="App">
                <section className="nes-container with-title">
                    <h2 className="title">Login Page</h2>
                    <div className="containers">

                        <p className={'caption'}>Please Enter your info to see the attendance</p>
                        <div className={'wrapper'}>
                            <i className="nes-kirby"/>
                        </div>

                        <div className="nes-field">
                            <label htmlFor="name_field">Username</label>
                            <input type="text" id="name_field" className="nes-input"/>

                            <label htmlFor="name_field">Password</label>
                            <input type="password" id="pass_field" className="nes-input"/>
                        </div>

                        <br/>

                        <div className={'wrapper'}>
                            <button type="button" className="nes-btn" onClick={this.fetchData}>Login</button>
                        </div>


                    </div>
                </section>
            </div>
        );
    }
}

export default App;
